import React, { useEffect, useState } from "react";
import axios from "../Axios";
function EmployeeAttendance() {
  const [attendance, setAttend] = useState([{}]);
  const id = localStorage.getItem("currentuserid");
  useEffect(async () => {
    await axios.get(`employees/${id}`).then((res) => {
      setAttend(res.data.attendance);
    });
  }, [id]);
  return (
    <div>
      <h2>Attendance</h2>
      <h4>Total Attendance : {attendance.length}</h4>
      {attendance.map((att) => {
        const { date, attendance, aparture, departure } = att;
        return (
          <div>
            <div className="att-row">
              <div className="att-label">
                <span>date  </span> 
                <span>{date}</span>
                <span>:</span>
                <span className="separate">:</span>
              </div>
              <div className="att-label">
                <span>aparture </span>
                <span>:</span>
                <span>{aparture}</span>
                <span className="separate">:</span>
              </div>
              <div className="att-label">
                <span>departure </span>
                <span>:</span>
                <span>{departure}</span>
                <span className="separate">:</span>
              </div>

              <div className="att-label">
                <span>
                  {" "}
                  attendance {" "}
                  <b
                    style={{ color: attendance === "absent" ? "red" : "white" }}
                  >
                    {attendance}
                  </b>
                </span>
                <span></span>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default EmployeeAttendance;
