import React, { useEffect, useState } from "react";
import axios from "../Axios";
function EmployeeAttendance() {
  const [attendance, setAttend] = useState([{}]);
  const id = localStorage.getItem("currentuserid");
  useEffect(async () => {
    await axios.get(`employees/${id}`).then((res) => {
      setAttend(res.data.attendance);
    });
  }, [id]);
  return (
    <div>
      <h2>Attendance</h2>
      <h4>Total Attendance : {attendance.length}</h4>
      {attendance.map((att) => {
        const { date, attendance, aparture, departure } = att;
        return (
          <div>
            <div className="att-row">
              <span>date : {date}</span> <span>:</span>
              <span>aparture :</span>
              <span>{aparture}</span>
              <span>:</span>
              <span>departure :</span>
              <span>{departure}</span>
              <span>:</span>
              <span>
                {" "}
                attendance :{" "}
                <span
                  style={{ color: !attendance === "present" ? "red" : "white" }}
                >
                  {attendance}
                </span>
              </span>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default EmployeeAttendance;
