import React from "react";
import Feedback from "./Feedback";
import Hero from "./Hero";
import Nav from "./Nav";
import { Helmet } from "react-helmet";
import { useEffect, useState } from "react/cjs/react.development";
import axios from "./Axios";
function Home() {
  const [data, setData] = useState([]);
  const [backup,setBackup] = useState()
  useEffect(() => {
    axios.get("http://localhost:5555/employees").then((res) => {
      setData(res.data);
      setBackup(res.data)
    });
  }, []);
  const Search = (e) => {
    const input = e.target.value;
    const result = data.filter((e) => {
      const filterData =  Object.values(e).join().includes(input);
      data.length === 0 ?setData(e) : setData(filterData)
    });
    console.log(data);
  };
  return (
    <>
      <div>
        <Helmet>
          <title>Home</title>
        </Helmet>
        <Nav />
        <Hero />
        {/* <Feedback /> */}
      </div>
      <div style={{ textAlign: "center" }}>
        <input type="text" placeholder="search..." onChange={Search} />
        <div>
          {data.map((value) => (
            <div key={value.id} style={{ margin: "20px", textAlign: "center" }}>
              <h3>{value.fullname}</h3>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Home;
